mapper.select:
select *
from t_user
where phone = #{phone}
   or email = #{email}

mapper.update:
update t_user set

    phone = #{phone},


    email = #{email},


    password = #{password},

updateTime = #{updateTime}
where id = #{id}

mapper.select:
select * from t_user_info where 1=1
and nick like concat('%', #{nick}, '%')
order by id desc limit #{start}, #{limit}
